    return (
      <ul className = "taskList">
        {this.tasks.map((task) =>
          <TaskItem key={task.id} item={task} />
        )}
      </ul>
    );
  // }
}

function handleCheckbox () {
  console.log("checkbox clicked");
} 

function TaskItem(props) {
  return (
    <li>
      <input type="checkbox" value={props.item.completed} onChange={handleCheckbox} />
      {props.item.name}
      </li>
  );
}

