//import logo from './logo.svg';
//import './App.css';
import React from 'react';

const BACKEND_URL = "http://127.0.0.1:3030/";
const GET_API = "tasks";

const demo_tasks = [
  { id: 0, name: "task number one", completed: 0 },
  { id: 1, name: "task number two", completed: 0 },
  { id: 2, name: "task number three", completed: 0 },
  { id: 3, name: "task number four", completed: 0 },
];

function App() {
  console.log("app started");
  return (
    <Checklist tasks={demo_tasks} />
  );
}

class Checklist extends React.Component {
  constructor(props) {
    super(props);
    this.tasks = props.tasks;
  }

  render() {
    // props = this.props;
    console.log("tasks", this.tasks);
    return (
      <ul className = "taskList">
        {this.tasks.map((task) =>
          <TaskItem key={task.id} item={task} />
        )}
      </ul>
    );
  }
}

function handleCheckbox () {
  console.log("checkbox clicked");
} 

function TaskItem(props) {
  console.log("id", props.item.id);
  return (
    <li>
      <input type="checkbox" value={props.item.completed} onChange={handleCheckbox} />
      {props.item.name}
      </li>
  );
}

