const BACKEND_URL = "http://127.0.0.1:3030/";
const GET_API = "tasks";

const demo_tasks = [
  { id: 0, name: "task number one", completed: 0 },
  { id: 1, name: "task number two", completed: 0 },
  { id: 2, name: "task number three", completed: 0 },
  { id: 3, name: "task number four", completed: 0 },
];

